Class('class.command.Compile', {
    requires: ['class.compile.Compile'],

    name:'compile',
    desc: 'compile',
    args: {
        optimize: [],
        md5: [],
        pack: [],
        empty: [],
        unique: [],
        verbose: [],
        file: ['-f', '--file', 'file name']
    },


    execute: function(args) {
        console.log(this.name + ' exe');


        var fs = require('fs');
        fs.readFile('tmp.html', 'utf8', function (err, data) {
            if (err) throw err;
            var code = "{%block " + data + "%}";
            var cmp = New('class.compile.Compile');


            fs.writeFile('cache.txt', cmp.compile(code, {parent:null, name:'tmp.html'}), function (err) {
                if (err) throw err;
                console.log('It\'s saved!'); //文件被保存
            });
        });

    }

});