Set.Class('classes.compile.Constants', {
    statics: {
        OPENBOUND:'{%',
        CLOSEBOUND: '%}',
        KEYWORD: {
            cond:1, str:1, echo:1, getv:1, code:1, script:1, file:1,
            for:0, foreach:0, html:0, block:0, head:0, body:0, phpend:0,
            if:3, elseif:3, else:3
        },
        TYPE_OPEN : 1,
        TYPE_CLOSE : 2,
        TYPE_DEFAULT: 0,
        isOpenDelimiter: function(del) {
            return del==classes.compile.Constants.OPENBOUND;
        },
        isCloseDelimiter: function(del) {
            return del==classes.compile.Constants.CLOSEBOUND;
        },
        isDelimiterBound: function(cur) {
            return cur==classes.compile.Constants.BOUNDCHAR;
        }
    }
});
